#!/usr/bin/env python3
import sys


def solve(ABCD: str):
    from itertools import product
    base = '{}{{}}{}{{}}{}{{}}{}'.format(*ABCD)
    for ops in product('+-', repeat=3):
        exp = base.format(*ops)
        if eval(exp) == 7:
            return exp + '=7'
    raise


# Generated by 1.1.6 https://github.com/kyuridenamida/atcoder-tools
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    ABCD = next(tokens)
    print(solve(ABCD))

def test():
    import doctest
    doctest.testmod()

if __name__ == '__main__':
    #test()
    main()
